<!DOCTYPE html><html lang="en">    <head>        <meta charset="utf-8">        <meta http-equiv="X-UA-Compatible" content="IE=edge">        <meta name="viewport" content="width=device-width, initial-scale=1">        <!-- The above 3 meta tags *must* come first in the head; any other head content must come *after* these tags -->        <meta name="description" content="">        <meta name="author" content="">        <link rel="icon" href="<?= site_url("library/images/hotel-flat-icon-vector.jpg") ?>">        <title>Room's Master List</title>        <link href="<?= site_url("library/css/bootstrap.min.css") ?>" rel="stylesheet" type="text/css"/>        <link href="<?= site_url("library/css/datatables.min.css") ?>" rel="stylesheet" type="text/css"/>        <link href="<?= site_url("assets/css/custom02.css") ?>" rel="stylesheet">        <style>            #modalDialog{                width:60%;            }        </style>    </head>    <body>        <?= $head02Temp?>        <div class="container-fluid">            <div class="row">                <?= $leftmenu02Temp?>                <div class="col-md-10 col-lg-offset-2">                    <div class="h3"><span>Hotel's Item Master List</span><span class="addbttn"><button onclick="addHotelItem()" class="btn btn-info">Add Room Item</button></span></div>                    <table id="hotelItem_list" class="table table-bordered table-striped table-hover">                        <thead>                            <tr>                                                               <th>Item Name</th>                                 <th>Category</th>                                 <th>Sub - Category</th>                                                                 <th>Item Attribute</th>                                 <th>Actions</th>                            </tr>                        </thead>                        <tbody>                        </tbody>                    </table>                </div>            </div>        </div>        <div id="hotelItemDetails" class="modal  fade" role="dialog">            <div id="modalDialog" class="modal-dialog  modal-lg">                <!-- Modal content-->                <div class="modal-content ">                    <div class="modal-header">                        <button type="button" class="close" data-dismiss="modal">&times;</button>                        <h4 class="modal-title">Hotel's Item Master List</h4>                    </div>                    <div class="modal-body">                        <form method="post" name="hotelItemEdit" id="hotelItemEdit" >                            <div class="row">                                <div class="form-group col-md-6 mb-6">                                     <input type="hidden" name="item_id" id="item_id" value="0" class="form-control">                                    <label for="item_name">Item Name</label>                                    <input type="text" name="item_name" id="item_name" class="form-control">                                    <div id='item_nameError' class="errorlabel"></div>                                    <label for="item_cat">Item Category:</label>                                    <select class="form-control" id="item_cat" name="item_cat"></select>                                    <div id='item_catError' class="errorlabel"></div>                                    <label for="item_subcat">Item Sub Category:</label>                                    <select class="form-control" id="item_subcat" name="item_subcat"></select>                                    <div id='item_subcatError' class="errorlabel"></div>                                </div>                                <div class="form-group col-md-6 mb-6">                                    <label for="item_attr">Item Attribute</label>                                    <input type="text" name="item_attr" id="item_attr" class="form-control">                                    <div id='item_attrError' class="errorlabel"></div>                                    <label for="item_desc">Description</label>                                    <textarea name="item_desc" class="form-control" rows="4" cols="" id="item_desc"></textarea>                                    <div id='item_descError' class="errorlabel"></div>                                </div>                            </div>                                                        <div class="row">                                <div class="form-group col-md-4 mb-3">                                    <input id="submitBtn" type="button" class="btn btn-info" value="submit" >                                </div>                            </div>                        </form>                    </div>                </div>            </div>        </div>        <script src="<?= site_url("library/js/jquery.min.js") ?>" type="text/javascript"></script>        <script src="<?= site_url("library/js/bootstrap.min.js") ?>" type="text/javascript"></script>        <script src="<?= site_url("library/js/jquery.validate.min.js") ?>" type="text/javascript"></script>        <script src="<?= site_url("library/js/datatables.min.js") ?>" type="text/javascript"></script>        <script type="text/javascript">                        var dataItemTable = "<?= site_url("hotel_items/ajaxAllHotelItemMasterDataTable") ?>";                        var hotelItemDetails = $("#hotelItemDetails");                        var itemCategory;                        var itemSubCategory;                       $(document).ready(function () {                            window.history.forward(-1);                            itemCategory = "<?= addslashes(json_encode($itemCategory['category'])) ?>";                            itemSubCategory = "<?= addslashes(json_encode($itemCategory['sub_category'])) ?>";                            var table1 = $('#hotelItem_list').DataTable({                                "ajax": {                                    url: dataItemTable,                                    type: 'GET'                                },                                "oLanguage": {                                    "sEmptyTable": "No Record Found"                                },                                "aoColumns": [                                    {mData: 'item_name'},                                    {mData: 'item_cat'},                                    {mData: 'item_subcat'},                                    {mData: 'item_attr'},                                    {mData: "item_id", bSortable: false, sWidth: "80px",                                        mRender: function (data, type, full) {                                            var editBtn = "<button class=\"btn btn-info btn-xs\" onclick=\"editHotelItem(" + data + ")\">Edit</button>";                                            var delBtn = "<button class=\"btn btn-danger btn-xs\" onclick=\"deleteHotelItem(" + data + ")\">Delete</button>";                                            return editBtn + "&nbsp;&nbsp;&nbsp;&nbsp;" + delBtn;                                        }                                    }                                ]                            });                        });                        function addHotelItem() {                            $("#hotelItemDetails input:not(#submitBtn)").val("");                            $("#hotelItemDetails option").removeAttr("selected");                            $("#hotelItemEdit")[0].reset();                            popCategory();                            popSubCategory();                            hotelItemDetails.modal("show");                        }                        function editHotelItem(item_id) {                            $.ajax({                                type: "POST",                                url: "<?= site_url('hotel_items/ajaxRoomItemMasterDetails') ?>",                                data: {item_id: item_id},                                success: function (result) {                                    var data = $.parseJSON(result);                                    $("input[name*='item_name']").val(data['item_name']);                                    $("input[name*='item_id']").val(data['item_id']);                                                                        $("input[name*='item_attr']").val(data['item_attr']);                                                                        $("#item_desc").html(data['item_desc']);                                    popCategory(data['item_cat']);                                    popSubCategory(data['item_cat'], data['item_subcat']);                                    hotelItemDetails.modal("show");                                }                            });                        }                        function refreshTable() {                            $.getJSON(dataItemTable, null, function (json) {                                table = $("#hotelItem_list").dataTable();                                oSettings = table.fnSettings();                                table.fnClearTable(this);                                for (var i = 0; i < json['data'].length; i++) {                                    table.oApi._fnAddData(oSettings, json['data'][i]);                                }                                oSettings.aiDisplay = oSettings.aiDisplayMaster.slice();                                table.fnDraw();                            });                        }                        function deleteHotelItem(item_id) {                            var r = confirm("Do you really want to delete this record?");                            if (r == true) {                                $.ajax({                                    type: "POST",                                    url: "<?= site_url('hotel_items/ajaxHotelItemMasterDelete') ?>",                                    data: {item_id: item_id},                                    success: function (result) {                                        hotelItemDetails.modal("hide");                                        refreshTable();                                    }                                });                            } else {                                roomItemDetails.modal("hide");                            }                        }                        $("#submitBtn").on("click", function () {                            var errorNo = 0;                            $(".errorlabel").html("");                            if ($("input[name*='item_name']").val().length < 1) {                                errorNo++;                                $("#item_nameError").html("Required");                            }else {                                $.ajax({                                    type: "POST",                                    async: false,                                    url: "<?= site_url('hotel_items/ajaxUniqueItemsHotelAttr') ?>",                                    data: {                                        primaryVal: $("#item_id").val(),                                        attr: "item_name",                                        attrVal: $("#item_name").val()                                    },                                    success: function (result) {                                        if (result > 0) {                                            $("#item_nameError").html("Item Name must be unique");                                            errorNo++;                                        }                                    }                                });                            }                            if ($("input[name*='item_attr']").val().length < 1) {                                errorNo++;                                $("#item_attrError").html("Required");                            }                            if ($("#item_cat").val().length < 1) {                                errorNo++;                                $("#item_catError").html("Required");                            }                            if(errorNo == 0){                                $("#hotelItemEdit").submit();                            }                        });                        $("#hotelItemEdit").submit(function (e) {                                $.ajax({                                    type: "POST",                                    url: "<?= site_url('hotel_items/ajaxRoomItemsMasterSubmit') ?>",                                    data: $("#hotelItemEdit").serialize(),                                    success: function (result) {                                        $("#hotelItemEdit")[0].reset();                                        hotelItemDetails.modal("hide");                                        refreshTable();                                    }                                });                            e.preventDefault();                        });                        function popCategory(id = "") {                            var category = $.parseJSON(itemCategory);                            var option = "<option value=\"\">Choose...</option>";                            $.each(category, function (key, row) {                                var select = id == key ? "selected='selected'" : "";                                option = option + "<option " + select + " value=\"" + key + "\">" + row + "</option>";                            });                            $("#item_cat").html(option);                        }                        function popSubCategory(id = "", opt = "") {                            var subCategory = $.parseJSON(itemSubCategory);                            var option = "<option value=\"\">Choose...</option>";                            if (id !== "") {                                $.each(subCategory[id], function (key, row) {                                    var select = opt == key ? "selected='selected'" : "";                                    option = option + "<option " + select + " value=\"" + key + "\">" + row + "</option>";                                });                            }                            $("#item_subcat").html(option);                        }                        $("#item_cat").on("change", function () {                            var valueSelected = this.value;                            popSubCategory(valueSelected);                        });        </script>    </body></html>