<!DOCTYPE html><html lang="en">    <head>        <meta charset="utf-8">        <meta http-equiv="X-UA-Compatible" content="IE=edge">        <meta name="viewport" content="width=device-width, initial-scale=1">        <!-- The above 3 meta tags *must* come first in the head; any other head content must come *after* these tags -->        <meta name="description" content="">        <meta name="author" content="">        <link rel="icon" href="<?= site_url("library/images/hotel-flat-icon-vector.jpg") ?>">        <title>Room's Master List</title>        <link href="<?= site_url("library/css/bootstrap.min.css") ?>" rel="stylesheet" type="text/css"/>        <link href="<?= site_url("library/css/datatables.min.css") ?>" rel="stylesheet" type="text/css"/>        <link href="<?= site_url("assets/css/custom02.css") ?>" rel="stylesheet">        <style>            #modalDialog{                width:50%;            }        </style>    </head>    <body>         <?=$head01Temp?>          <div class="container-fluid">            <div class="row">                <?=$leftmenu01Temp?>                <div class="col-md-10 col-lg-offset-2">                    <div class="h3"><span>Room Management</span><span class="addbttn"><button onclick="addRoomType()" class="btn btn-info">Add Room Type</button></span></div>                    <table id="room_list" class="table table-bordered table-striped table-hover">                        <thead>                            <tr>                                <th>Room Type</th>   								<th>Description</th>                                   <th>Actions</th>                            </tr>                        </thead>                        <tbody>                        </tbody>                    </table>                </div>            </div>        </div>        <div id="roomDetails" class="modal fade" role="dialog">            <div id="modalDialog" class="modal-dialog modal-sm">                <!-- Modal content-->                <div class="modal-content ">                    <div class="modal-header">                        <button type="button" class="close" data-dismiss="modal">&times;</button>                        <h4 class="modal-title">Room Management</h4>                    </div>                    <div class="modal-body">                        <form method="post" name="roomDetailEdit" id="roomDetailEdit" >                            <div class="row">                                <div class="form-group col-md-10">                                    <label for="room_type">Room Type</label>                                    <input type="hidden" name="room_master_id" id="room_master_id" value="0" class="form-control">                                    <input type="text" name="room_type" id="room_type" class="form-control">                                    <div id='roomTypeArr' class="errorlabel"></div>                                </div>                            </div>                            <div class="row">                                <div class="form-group col-md-10">                                    <label for="room_type_Desc">Room Description</label>                                    <textarea name="room_type_Desc" class="form-control" rows="5" cols="" id="room_type_Desc"></textarea>                                    <div id='roomTypeDescArr' class="errorlabel"></div>                                </div>                            </div>                            <div class="row">                                <div class="form-group col-md-10">                                    <input id="submitBtn" type="button" class="btn btn-info" value="submit" >                                </div>                            </div>                        </form>                    </div>                  </div>            </div>        </div>        <script src="<?= site_url("library/js/jquery.min.js") ?>" type="text/javascript"></script>        <script src="<?= site_url("library/js/bootstrap.min.js") ?>" type="text/javascript"></script>        <script src="<?= site_url("library/js/jquery.validate.min.js") ?>" type="text/javascript"></script>        <script src="<?= site_url("library/js/datatables.min.js") ?>" type="text/javascript"></script>        <script type="text/javascript">                        var dataTableRoom = "<?= site_url("rooms/ajaxAllRoomMasterDataTable") ?>";                        var roomDetails = $("#roomDetails");                        $(document).ready(function () {                             window.history.forward(-1);                            var table1 = $('#room_list').DataTable({                                "ajax": {                                    url : dataTableRoom,                                    type : 'GET'                                },                                "aoColumns": [                                    {mData: 'room_type'},									{mData: 'room_type_Desc'},                                    {mData: "room_master_id", bSortable: false, sWidth: "80px",                                        mRender: function (data, type, full) {                                            var editBtn = "<button class=\"btn btn-info btn-xs\" onclick=\"editRoom(" + data + ")\">Edit</button>";                                            var delBtn = "<button class=\"btn btn-danger btn-xs\" onclick=\"deleteRoom(" + data + ")\">Delete</button>";                                            return editBtn + "&nbsp;&nbsp;&nbsp;&nbsp;" + delBtn;                                        }                                    }                                ]                            });                        });                        function addRoomType() {                            $("#roomDetailEdit input:not(#submitBtn)").val("");                            $("#roomDetailEdit textarea").html("");                            $("#roomDetailEdit")[0].reset();                            $(".errorlabel").html("");                            roomDetails.modal("show");                        }                        function editRoom(room_master_id) {                            $.ajax({                                type: "POST",                                url: "<?= site_url('rooms/ajaxRoomMasterDetails') ?>",                                data: {room_master_id: room_master_id},                                success: function (result) {                                    var data = $.parseJSON(result);                                    $("input[name*='room_type']").val(data['room_type']);                                    $("#room_type_Desc").html(data['room_type_Desc']);                                    $("input[name*='room_master_id']").val(data['room_master_id']);                                    $(".errorlabel").html("");                                    roomDetails.modal("show");                                }                            });                        }                        function refreshTable() {                            $.getJSON(dataTableRoom, null, function (json) {                                table = $('#room_list').dataTable();                                oSettings = table.fnSettings();                                table.fnClearTable(this);                                for (var i = 0; i < json['data'].length; i++) {                                    table.oApi._fnAddData(oSettings, json['data'][i]);                                }                                oSettings.aiDisplay = oSettings.aiDisplayMaster.slice();                                table.fnDraw();                            });                        }                        function deleteRoom(room_master_id) {                            var r = confirm("Do you really want to delete this record?");                            if (r == true) {                                $.ajax({                                    type: "POST",                                    url: "<?= site_url('rooms/ajaxRoomMasterDelete') ?>",                                    data: { room_master_id : room_master_id },                                    success: function (result) {                                        roomDetails.modal("hide");                                        refreshTable();                                    }                                });                            } else {                                roomDetails.modal("hide");                            }                        }                        $("#submitBtn").on("click", function () {                         $(".errorlabel").html("");                            var errorNo = 0;                            if($("#room_type").val().length == 0){                                $("#roomTypeArr").html("Required");                                 errorNo++;                            }else{                                 $.ajax({                                    type: "POST",                                    async: false,                                    url: "<?= site_url('rooms/ajaxUniqueRoomsAttr') ?>",                                    data: {                                        primaryVal : $("#room_master_id").val(),                                        attr : "room_type",                                        attrVal : $("#room_type").val()                                    },                                    success: function (result) {                                        if(result > 0){                                                                                     $("#roomTypeArr").html("Room type must be unique");                                            errorNo++;                                        }                                    }                                });                            }                               if (errorNo === 0) {                                $("#roomDetailEdit").submit();                            }                        });                        $("#roomDetailEdit").submit(function (e) {                                $.ajax({                                    type: "POST",                                    url: "<?= site_url('rooms/ajaxRoomMasterSubmit') ?>",                                    data: $("#roomDetailEdit").serialize(),                                    success: function (result) {                                        $("#roomDetailEdit")[0].reset();                                        roomDetails.modal("hide");                                        refreshTable();                                    }                                });                            e.preventDefault();                        });        </script>    </body></html>